using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class TransformationTalisman : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Transformation Talisman");			Tooltip.SetDefault("Turns the holder into merfolk upon entering water and lava\nTurns the holder into a werewolf at night");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/TransformationTalisman");			item.defense = 2;			item.rare = 9;			item.width = dims.Width;			item.value = Item.sellPrice(0, 10, 0, 0);			item.accessory = true;			item.height = dims.Height;		}		public override void UpdateAccessory(Player player, bool hideVisual)		{			var flag2 = Collision.LavaCollision(player.position, player.width, player.height);			if (flag2)			{				player.GetModPlayer<ExxoAvalonOriginsModPlayer>().mermanLava = true;				player.accFlipper = true;				player.lavaImmune = true;			}			player.fireWalk = true;			player.ignoreWater = true;			player.accMerman = true;			player.wolfAcc = true;			player.lavaImmune = true;			player.waterWalk = true;		}	}}