using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class Trifectal : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Trifectal");			Tooltip.SetDefault("Splits into three balls");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/Trifectal");			item.damage = 75;			item.noUseGraphic = true;			item.channel = true;			item.scale = 1.5f;			item.shootSpeed = 15f;			item.crit += 8;			item.rare = 8;			item.noMelee = true;			item.width = dims.Width;			item.useTime = 45;			item.knockBack = 8f;			item.shoot = ModContent.ProjectileType<Projectiles.TrifectalBall>();			item.melee = true;			item.value = Item.sellPrice(0, 15, 70, 0);			item.useStyle = 5;			item.useAnimation = 45;			item.height = dims.Height;		}	}}