using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class TritanoriumBroadsword : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Tritanorium Broadsword");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/TritanoriumBroadsword");			item.damage = 110;			item.autoReuse = true;			item.useTurn = true;			item.scale = 1.2f;			item.rare = 11;			item.width = dims.Width;			item.useTime = 16;			item.knockBack = 15f;			item.melee = true;			item.useStyle = 1;			item.value = Item.sellPrice(2, 10, 0, 0);			item.useAnimation = 16;			item.height = dims.Height;		}	}}