using Microsoft.Xna.Framework;using Microsoft.Xna.Framework.Graphics;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class TroxiniumBar : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Troxinium Bar");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/TroxiniumBar");			item.autoReuse = true;			item.useTurn = true;			item.maxStack = 999;			item.consumable = true;			item.createTile = TileID.MetalBars;			item.placeStyle = 32;			item.rare = 5;			item.width = dims.Width;			item.useTime = 10;			item.value = Item.sellPrice(0, 0, 75, 0);			item.useStyle = 1;			item.useAnimation = 15;			item.height = dims.Height;		}
		public override Color? GetAlpha(Color lightColor)		{			return new Color(255, 255, 255, 255);		}
		public override void PostDrawInWorld(SpriteBatch spriteBatch, Color lightColor, Color alphaColor, float rotation, float scale, int whoAmI)		{			Texture2D texture = mod.GetTexture("Items/TroxiniumBar_Glow");			spriteBatch.Draw			(				texture,				new Vector2				(					item.position.X - Main.screenPosition.X + item.width * 0.5f,					item.position.Y - Main.screenPosition.Y + item.height - texture.Height * 0.5f + 2f				),				new Rectangle(0, 0, texture.Width, texture.Height),				Color.White,				rotation,				texture.Size() * 0.5f,				scale,				SpriteEffects.None,				0f			);		}	}}