using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class TroxiniumPickaxe : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Troxinium Pickaxe");			Tooltip.SetDefault("Can mine Ferozium");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/TroxiniumPickaxe");			item.damage = 14;			item.autoReuse = true;			item.useTurn = true;			item.scale = 1f;			item.pick = 185;			item.rare = 5;			item.width = dims.Width;			item.useTime = 20;			item.knockBack = 1f;			item.melee = true;			item.useStyle = 1;			item.value = Item.sellPrice(0, 3, 40, 0);			item.useAnimation = 20;			item.height = dims.Height;		}	}}