using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class TroxiniumWaraxe : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Troxinium Waraxe");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/TroxiniumWaraxe");			item.damage = 32;			item.autoReuse = true;			item.useTurn = true;			item.scale = 1f;			item.axe = 20;			item.rare = 5;			item.width = dims.Width;			item.useTime = 18;			item.knockBack = 6f;			item.melee = true;			item.useStyle = 1;			item.value = Item.sellPrice(0, 3, 40, 0);			item.useAnimation = 18;			item.height = dims.Height;		}	}}