using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class TrueAeonsEternity : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("True Aeon's Eternity");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/TrueAeonsEternity");			item.damage = 69;			item.autoReuse = true;			item.useTurn = true;			item.scale = 1.1f;			item.shootSpeed = 8f;			item.rare = 8;			item.width = dims.Width;			item.useTime = 24;			item.knockBack = 5f;			item.shoot = ModContent.ProjectileType<Projectiles.AeonBeam>();			item.melee = true;			item.useStyle = 1;			item.value = Item.sellPrice(0, 3, 0, 0);			item.useAnimation = 24;			item.height = dims.Height;		}		public override bool Shoot(Player player, ref Vector2 position, ref float speedX, ref float speedY, ref int type, ref int damage,			ref float knockBack)		{			if (Main.rand.Next(25) == 0)			{				for (int num185 = 0; num185 < 6; num185++)				{					float num186 = speedX;					float num187 = speedY;					num186 += (float)Main.rand.Next(-40, 41) * 0.05f;					num187 += (float)Main.rand.Next(-40, 41) * 0.05f;					Projectile.NewProjectile(position.X, position.Y, num186, num187, ProjectileID.Starfury, damage, knockBack, player.whoAmI, 0f, 0f);				}			}			return false;		}	}}