using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class UnvolanditeAnvil : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Unvolandite Anvil");			Tooltip.SetDefault("Used to craft shm items");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/UnvolanditeAnvil");			item.autoReuse = true;			item.consumable = true;			item.createTile = ModContent.TileType<Tiles.UnvolanditeAnvil>();			item.rare = 9;			item.width = dims.Width;			item.useTurn = true;			item.useTime = 10;			item.useStyle = 1;			item.maxStack = 99;			item.value = 100000;			item.useAnimation = 15;			item.height = dims.Height;		}	}}