using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Projectiles{	public class AquaBlast : ModProjectile	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Aqua Blast");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Projectiles/AquaBlast");			projectile.width = dims.Width * 18 / 96;			projectile.height = dims.Height * 18 / 96 / Main.projFrames[projectile.type];			projectile.aiStyle = -1;			projectile.friendly = true;			projectile.light = 1f;			projectile.magic = true;			projectile.penetrate = 1;			projectile.scale = 1f;			projectile.tileCollide = false;			Main.projFrames[projectile.type] = 3;		}		public override void AI()		{			if (projectile.localAI[0] == 0f)			{				projectile.spriteDirection = Main.player[projectile.owner].direction;				var num975 = ExxoAvalonOriginsGlobalNPC.FindClosest(new Vector2(Main.mouseX + Main.screenPosition.X, Main.mouseY + Main.screenPosition.Y), 80f);				if (num975 != -1)				{					projectile.position = new Vector2(Main.npc[num975].position.X + Main.player[projectile.owner].direction * 50, Main.npc[num975].position.Y - 200f);					projectile.velocity = Vector2.Normalize(Main.npc[num975].Center - projectile.Center) * 10f;				}				projectile.localAI[0] = 1f;			}			projectile.frameCounter++;			if (projectile.frameCounter > 8)			{				projectile.frame++;				projectile.frameCounter = 0;			}			else if (projectile.frame > 3)			{				projectile.frame = 1;			}			if (projectile.frame < 1)			{				projectile.frame = 1;				return;			}		}    }}