using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Projectiles{	public class ChlorophyteStar : ModProjectile	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Chlorophyte Star");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Projectiles/ChlorophyteStar");			projectile.width = dims.Width;			projectile.height = dims.Height / Main.projFrames[projectile.type];			projectile.aiStyle = -1;			projectile.friendly = true;			projectile.penetrate = 5;			projectile.light = 0.5f;			projectile.alpha = 0;			projectile.scale = 1f;			projectile.timeLeft = 3600;			projectile.ranged = true;			projectile.tileCollide = true;        }        public override void AI()        {            projectile.rotation += (Math.Abs(projectile.velocity.X) + Math.Abs(projectile.velocity.Y)) * 0.03f * projectile.direction;            projectile.ai[0] += 1f;            if (projectile.ai[0] >= 20f)            {                projectile.velocity.Y = projectile.velocity.Y + 0.4f;                projectile.velocity.X = projectile.velocity.X * 0.97f;            }            if (projectile.velocity.Y > 16f)            {                projectile.velocity.Y = 16f;            }        }    }}