using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Projectiles{	public class DarkFlame : ModProjectile	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Dark Flame");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Projectiles/DarkFlame");			projectile.width = dims.Width;			projectile.height = dims.Height / Main.projFrames[projectile.type];			projectile.aiStyle = -1;			projectile.hostile = true;			projectile.light = 0.8f;			projectile.alpha = 50;			projectile.magic = true;			projectile.penetrate = -1;			projectile.scale = 0.9f;		}        public override void AI()        {            var num150 = Dust.NewDust(new Vector2(projectile.position.X + projectile.velocity.X, projectile.position.Y + projectile.velocity.Y), projectile.width, projectile.height, 58, projectile.velocity.X, projectile.velocity.Y, 100, default(Color), 1f);            Main.dust[num150].noGravity = true;            var num151 = Dust.NewDust(new Vector2(projectile.position.X + projectile.velocity.X, projectile.position.Y + projectile.velocity.Y), projectile.width, projectile.height, 36, projectile.velocity.X, projectile.velocity.Y, 100, default(Color), 1f);            Main.dust[num151].noGravity = true;            if (projectile.ai[1] >= 20f)            {                projectile.velocity.Y = projectile.velocity.Y + 0.2f;            }            projectile.rotation += 0.3f * projectile.direction;            if (projectile.velocity.Y > 16f)            {                projectile.velocity.Y = 16f;            }        }    }}