using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Projectiles{	public class NaquadahTrident : ModProjectile	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Naquadah Trident");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Projectiles/NaquadahTrident");			projectile.width = dims.Width * 18 / 120;			projectile.height = dims.Height * 18 / 120 / Main.projFrames[projectile.type];			projectile.aiStyle = -1;			projectile.friendly = true;			projectile.penetrate = -1;			projectile.tileCollide = false;			projectile.scale = 1.1f;			projectile.hide = true;			projectile.ownerHitCheck = true;			projectile.melee = true;		}		public override void AI()		{			projectile.direction = Main.player[projectile.owner].direction;			Main.player[projectile.owner].heldProj = projectile.whoAmI;			Main.player[projectile.owner].itemTime = Main.player[projectile.owner].itemAnimation;			projectile.position.X = Main.player[projectile.owner].position.X + Main.player[projectile.owner].width / 2 - projectile.width / 2;			projectile.position.Y = Main.player[projectile.owner].position.Y + Main.player[projectile.owner].height / 2 - projectile.height / 2;			if (!Main.player[projectile.owner].frozen)			{				if (projectile.ai[0] == 0f)				{					projectile.ai[0] = 3f;					projectile.netUpdate = true;				}				if (Main.player[projectile.owner].itemAnimation < Main.player[projectile.owner].itemAnimationMax / 3)				{					projectile.ai[0] -= 2.4f;				}				else				{					projectile.ai[0] += 2.1f;				}			}			projectile.position += projectile.velocity * projectile.ai[0];			if (Main.player[projectile.owner].itemAnimation == 0)			{				projectile.Kill();			}			projectile.rotation = (float)Math.Atan2(projectile.velocity.Y, projectile.velocity.X) + 2.355f;			if (projectile.spriteDirection == -1)			{				projectile.rotation -= 1.57f;			}		}	}}