using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;using Terraria.ObjectData;using Terraria.Localization;namespace ExxoAvalonOrigins.Tiles{	public class OsmiumOre : ModTile	{		public override void SetDefaults()		{			mineResist = 2f;			AddMapEntry(new Color(0, 148, 255), LanguageManager.Instance.GetText("Osmium"));			Main.tileSolid[Type] = true;			Main.tileBlockLight[Type] = true;            Main.tileSpelunker[Type] = true;            Main.tileValue[Type] = 430;            drop = mod.ItemType("OsmiumOre");            soundType = SoundID.Tink;            soundStyle = 1;            minPick = 60;            dustType = 156;
        }	}}