using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;using Terraria.ObjectData;namespace ExxoAvalonOrigins.Tiles{	public class PlasmaLamp : ModTile	{		public override void SetDefaults()		{			TileObjectData.newTile.Width = 2;			TileObjectData.newTile.Height = 2;			TileObjectData.newTile.CoordinateWidth = 16;			TileObjectData.newTile.CoordinateHeights = new int[] {16, 16};			TileObjectData.newTile.CoordinatePadding = 2;			TileObjectData.newTile.DrawYOffset = 2;			TileObjectData.newTile.StyleHorizontal = true;			TileObjectData.addTile(Type);			Main.tileLighted[Type] = true;			Main.tileFrameImportant[Type] = true;			drop = mod.ItemType("PlasmaLamp");		}        public override void ModifyLight(int i, int j, ref float r, ref float g, ref float b)        {            r = 0.6f;            g = 0.1f;            b = 0.6f;        }    }}