using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;using Terraria.ObjectData;using Terraria.Localization;namespace ExxoAvalonOrigins.Tiles{	public class TritanoriumOre : ModTile	{		public override void SetDefaults()		{			AddMapEntry(Color.DarkGreen, LanguageManager.Instance.GetText("Tritanorium Ore"));			Main.tileSolid[Type] = true;            Main.tileSpelunker[Type] = true;            drop = mod.ItemType("TritanoriumOre");            soundType = SoundID.Tink;            soundStyle = 1;            minPick = 210;        }        public override bool CanExplode(int i, int j)
        {
            return false;
        }
    }}