using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;using Terraria.ObjectData;namespace ExxoAvalonOrigins.Tiles{	public class YellowIce : ModTile	{		public override void SetDefaults()		{			AddMapEntry(new Color(234, 204, 0));			Main.tileSolid[Type] = true;			Main.tileBlockLight[Type] = true;            Main.tileMerge[Type][TileID.IceBlock] = true;
